mapper.update:
update channel
channel_title = #{title}, channel_description = #{description}, channel_create_date = #{createDate}, channel_update_date = #{updateDate}, channel_delete_yn = #{deleteYn}, channel_image_name = #{imageName},
channel_delete_date = #{deleteDate}
where channel_id = #{id}

mapper.delete:
delete from
    channel_participant
where
    channel_id = #{channel.id}
    and user_id = #{user.id}
